import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../product.service';
import { Product } from '../product.model';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})

export class ProductDetailComponent implements OnInit {
  productForm: FormGroup;
  product: Product;
  sku: string;
  submitted = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private formBuilder: FormBuilder,
    private productService: ProductService
  ) { }

  ngOnInit(): void {
    this.productForm = this.formBuilder.group({
      name: ['', Validators.required],
      type: ['', Validators.required],
      description: ['', Validators.maxLength(56)],
      color: ['', Validators.maxLength(56)],
      price: ['', [Validators.required, Validators.min(0)]]
    });
    this.route.paramMap.subscribe(params => {
      this.sku = params.get('sku');
      this.productService.getProduct(this.sku).subscribe(product => {
        this.product = product;
        this.productForm.patchValue(this.product);
      });
    });
  }

  get form() { return this.productForm.controls; }

  onSubmit() {
    this.submitted = true;
    if (this.productForm.invalid) {
      return;
    }
    const productData = this.productForm.value;
    productData.sku = this.sku;
    this.productService.updateProduct(productData).subscribe(() => {
      this.router.navigate(['/product-list']);
    });
  }
}
