import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';

import { Product } from './product.model';


@Injectable({
  providedIn: 'root',
})
export class ProductService {

  private productsUrl = 'assets/product-fixtures.json';
  private products: Product[] = [];



  constructor(private http: HttpClient) {}

  getProducts(): Observable<Product[]> {
    return this.http.get<Product[]>(this.productsUrl);
  }

  getProduct(sku: string): Observable<Product> {
    return this.http.get<Product>(`${this.productsUrl}?sku=${sku}`);
  }

  // getProduct(sku: string): Observable<Product> {
  //   return this.getProducts().pipe(
  //     map(products => products.find(product => product.sku === sku))
  //   );

  // }

  updateProduct(product: Product): Observable<void> {
    return this.http.put<void>(`${this.productsUrl}/${product.id}`, product);
  }

  deleteProduct(sku: string): Observable<void> {
    return this.http.delete<void>(`${this.productsUrl}/${sku}`);
  }

