import { Component, OnInit } from '@angular/core';
import { Product } from '../product.model';
import { FormsModule } from '@angular/forms';
import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ProductService } from '../product.service';
import { IDropdownSettings } from 'ng-multiselect-dropdown';


@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent implements OnInit {
  // colorFilter: string = '';
  filteredProducts: Product[] = [];
  selectedColors: string[] = ['blue', 'white'];

  dropdownList:any = [];
  selectedItems:any = [];
  dropdownSettings = {};

  // Pagination variables
  currentPage = 1;
  productsPerPage = 10;
  itemsPerPage: number = 10;

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.productService.getProducts().subscribe((data: Product[]) => {
      this.filteredProducts = data;

      data.map(product=>{
        this.dropdownList.push({
          text:product.color,
          value:product.color
        })


      })

    });
    this.dropdownList = [
      { item_id: 1, item_text: 'Mumbai' },
      { item_id: 2, item_text: 'Bangaluru' },
      { item_id: 3, item_text: 'Pune' },
      { item_id: 4, item_text: 'Navsari' },
      { item_id: 5, item_text: 'New Delhi' }
    ];
    console.log(this.dropdownList)

    this.dropdownSettings = {
      singleSelection: false,
      idField: 'value',
      textField: 'text',
      selectAllText: 'Select All',
      unSelectAllText: 'UnSelect All',
      itemsShowLimit: 3,
      allowSearchFilter: true
    };
  }

  onItemSelect(item: any) {
    console.log(item);
  }
  onSelectAll(items: any) {
    console.log(items);
  }

  deleteProduct(sku: string): void {
    this.productService.deleteProduct(sku).subscribe(() => {
      // Filter out the deleted product from the products array
      this.filteredProducts = this.filteredProducts.filter(
        (p) => p.sku !== sku
      );
    });
  }

  filterProductsByColor(): Product[] {
    let result: Product[] = [];
    for (let i = 0; i < this.selectedColors.length; i++) {
      const selectedColor = this.selectedColors[i];
      result = result.concat(
        this.filteredProducts.filter(
          (product) => product.color.toLowerCase().includes(selectedColor.toLowerCase())
        )
      );
    }
    // Remove duplicates
    return Array.from(new Set(result));
  }

  get filteredProductsWithColor(): Product[] {
    const startIndex = (this.currentPage - 1) * this.productsPerPage;
    const endIndex = startIndex + this.productsPerPage;
    let result: Product[] = [];
    if (this.selectedColors.length > 0) {
      result = this.filterProductsByColor();
    } else {
      result = this.filteredProducts;
    }
    return result.slice(startIndex, endIndex);
  }


  // get filteredProductsWithColor(): Product[] {
  //   const startIndex = (this.currentPage - 1) * this.productsPerPage;
  //   const endIndex = startIndex + this.productsPerPage;
  //   return this.filteredProducts
  //     .filter((product) =>
  //       product.color.toLowerCase().includes(this.colorFilter.toLowerCase())
  //     ).slice(startIndex, endIndex);

  // }

  get pages(): number[] {
    const pageCount = Math.ceil(this.filteredProducts.length / this.productsPerPage);
    return Array.from({ length: pageCount }, (_, i) => i + 1);
  }

}
