import { Component, OnInit } from '@angular/core';
import { Product } from '../product.model';
import { FormsModule } from '@angular/forms';
import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ProductService } from '../product.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent implements OnInit {
  colorFilter: string = '';
  filteredProducts: Product[] = [];
  currentPage = 1;
  productsPerPage = 10;

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.productService.getProducts().subscribe((data: Product[]) => {
      this.filteredProducts = data;
    });
  }

  deleteProduct(sku: string): void {
    this.productService.deleteProduct(sku).subscribe(() => {
      // Filter out the deleted product from the products array
      this.filteredProducts = this.filteredProducts.filter(
        (p) => p.sku !== sku
      );
    });

    console.log(this.filteredProducts);
  }

  get filteredProductsWithColor(): Product[] {
    // if (this.colorFilter.trim() === '') {
    //   // Return all products if the color filter is empty
    //   return this.filteredProducts;
    // } else {
    //   // Filter products based on color
    //   return this.filteredProducts.filter(p => p.color.toLowerCase().includes(this.colorFilter.toLowerCase()));
    // }
    const startIndex = (this.currentPage - 1) * this.productsPerPage;
    const endIndex = startIndex + this.productsPerPage;
    return this.filteredProducts
      .filter((product) =>
        product.color.toLowerCase().includes(this.colorFilter.toLowerCase())
      )
      .slice(startIndex, endIndex);
  }
}
