  deleteProduct(sku: string): Observable<void> {
    this.products = this.products.filter(product => product.sku !== sku);
    return of(undefined);
  }

  updateProduct(product: Product): Observable<any> {
    // const index = this.products.findIndex(p => p.sku === product.sku);
