import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../product.service';
import { Product } from '../product.model';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.scss']
})
export class ProductDetailComponent implements OnInit {
  sku: string = "";
  product: Product ={
    id: '',
    sku: '',
    name: '',
    type: '',
    description: '',
    color: '',
    price: 0
  };
  productForm: FormGroup;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private fb: FormBuilder,
    private productService: ProductService
  ) {
    this.productForm = this.fb.group({
      name: ['', Validators.required],
      type: ['', [Validators.required, Validators.maxLength(55)]],
      description: ['', [Validators.required, Validators.maxLength(55)]],
      color: ['', [Validators.required, Validators.maxLength(55)]],
      price: [0, [Validators.required, Validators.pattern('^-?[0-9]+(\.[0-9]*)?$'), Validators.min(0)]],
    });
  }

  ngOnInit(): void {
    this.sku = this.route.snapshot.params['sku'];
    this.getProduct(this.sku);
  }

  getProduct(sku: string): void {
    this.productService.getProduct(sku)
      .subscribe((product: Product) => {
        this.product = product;
        this.productForm.patchValue({
          name: product.name,
          type: product.type,
          description: product.description,
          color: product.color,
          price: product.price
        });
      });
  }

  updateProduct(): void {
    if (this.productForm.valid){
    const updatedProduct = {...this.product, ...this.productForm.value};
    this.productService.updateProduct(updatedProduct)
      .subscribe(() => this.router.navigate(['/product-list']));
  }else{
    console.error('form-invalid!!')

    console.log('Name:',this.productForm.get('name')?.invalid)
    console.log('type:',this.productForm.get('type')?.invalid)
    console.log('desc:',this.productForm.get('description')?.invalid)
    console.log('color:',this.productForm.get('color')?.invalid)
    console.log('price:',this.productForm.get('price')?.invalid)

  }
}
}
